import Layout from '../components/MyLayout';

class SearchInvitations extends React.Component {
  constructor(props) {
    super(props);
    this.state = {value: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.searchInvitationsForGuest = this.searchInvitationsForGuest.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value.toUpperCase()});
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    matchingInvitations = searchInvitationsForGuest(this.state.value)
    console.log(`Matching invitations: ${matchingInvitations}`)
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}

//TODO: broken - how to call async function from within react component?
async function searchInvitationsForGuest(searchString) {
  const invitationSearchResponse = await fetch('http://localhost:8080/guest_invitations_search', {
    method: 'GET',
    headers: {
      'Content-Type': 'application/json'
    },
    body: {
      'guest_search_string': '$searchString'
    }
  });
  return await invitationsSearchResponse.json()
}

export default function SearchInvitationsLayout() {
  return (
    <Layout>
      <br></br>
      <SearchInvitations/>
    </Layout>
  );
}
