import Layout from '../components/MyLayout';

class SearchInvitations extends React.Component {
  constructor(props) {
    super(props);
    this.state = {value: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value.toUpperCase()});
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}

async function searchInvitationsForGuest(searchString) {
  const invitationSearchResponse = await fetch('http://localhost:8080/guest_invitations_search', {
    method: 'GET',
    headers: {
      'Content-Type': 'application/json'
    },
    body: {
      'guest_search_string': '$searchString'
    }
  });
  return await invitationsSearchResponse.json()
}

GuestList.getInitialProps = async function() {
  const res = await fetch('http://localhost:8080/guests');
  const data = await res.json();

  console.log(`Guest data fetched. Count: ${data.length}`);

  return {
    guests: data
  };
};

export default function SearchInvitationsLayout() {
  return (
    <Layout>
      <br></br>
      <SearchInvitations/>
    </Layout>
  );
}
