import Layout from '../components/MyLayout';

class SearchInvitations extends React.Component {
  constructor(props) {
    super(props);
    this.state = {value: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.searchInvitationsForGuest = this.searchInvitationsForGuest.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value.toUpperCase()});
  }

  handleSubmit(event) {
    let searchString = this.state.value
    console.log(`Search String: ${searchString}`)
    let matchingInvitations = searchInvitationsForGuest(searchString)
    console.log(`Matching invitations: ${matchingInvitations}`)
    event.preventDefault();
  }

  //TODO: broken - how to call async function from within react component?
  searchInvitationsForGuest(searchString) {
    console.log(`in search invitations function`)
    const invitationSearchResponse = fetch('http://localhost:8080/guest_invitations_search', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json'
      },
      body: {
        'guest_search_string': '$searchString'
      }
    })
    .then((response) => response.json())
    .then((data) => {
      console.log('Success:', data);
    })
    .catch((error) => {
      console.error('Error:', error);
    });
    return invitationSearchResponse;
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}



export default function SearchInvitationsLayout() {
  return (
    <Layout>
      <br></br>
      <SearchInvitations/>
    </Layout>
  );
}
